import { Component } from "react/cjs/react.production.min";
import HeapOverFlowService from "../../api/heapoverflow/HeapOverFlowService";
import './Question.css'

export default class QuestionComponent extends Component{
    
    render(){
        return(
            <div className="card">
                <h4>{this.props.question.title}</h4>
                {this.props.question.description}
                <button className="custom-btn" onClick={this.props.onDeleteClicked}>Delete</button>
            </div>
        )
    }

    onQuestionDeleteCalled(question){
        new HeapOverFlowService().deleteQuestion(question.id);
    }
}
