import { Component } from "react/cjs/react.production.min"
import './login.css'

class Todo extends Component{
    constructor(props){
        super(props)
    }

    render(){
        return(
            <div className="todo">
                <LoginComponent/>
            </div>
        )
    }
}

class LoginComponent extends Component{
    #userNameAttr = 'username'
    #passwordAttr = 'password'

    constructor(props){
        super(props);
        this.state = {[this.#userNameAttr]:'', [this.#passwordAttr]:''};
        this.onInputChange = this.onInputChange.bind(this);
    }

    onInputChange(event) {
        this.setState({
            [event.target.name] : event.target.value
        });
        console.log(this.state);
    }

    render(){
        return(
            <div className="login">
                UserName <input type="text" name ={this.#userNameAttr} onChange={this.onInputChange}/>
                Password <input type="password" name ={this.#passwordAttr} onChange={this.onInputChange}/>
                <br/>
                <button type="submit" className="btn btn-success" style={{margin: '10px'}}>Login</button>
            </div>
        )
    }    
}

export default Todo;
