import React, { Component } from "react";
import './Count.css'
import PropTypes from 'prop-types'

class Count extends Component{
    constructor(){
        super();
        this.state = this.props.by
    }
    
    render(){
        return(
            <div>
            <button type="button" className="btn btn-warning" onClick={this.props.onCountClick(this.props.by)}>+{this.props.by}</button>
              {this.state.count}
            </div>
        );
    }

    onCountClick = () => {
        this.setState({count: this.state.count + this.props.by});
        console.log(this.state.count)
    }
}

Count.defaultProps = {
    by: 1
}

export default Count
