import logo from './logo.svg';
import './App.css';
import Creatie, {NotSoCreatie} from './components/creaty'
import Count from './components/counter/Count';
import 'bootstrap/dist/css/bootstrap.css';
import { Component } from 'react/cjs/react.production.min';

class App extends Component {

  imgSrc = "https://www.jeduka.com/storage/newsletter/1600436299_Top_100_Universities_in_USA.jpg";
  //let cnt = 15;

  constructor(props){
    super(props);
    this.state = {cnt:0};
  }  

  onCountClick = (by) => {
    //this.setState({cnt: this.state.cnt + by});
    console.log(this);
    this.setState(function(state,props){
      console.log(this)
      return {cnt : state.cnt + by};
    });
  };

  render(){
    return (
      <div className="App">
        <h1> Hello World </h1>
        <img src={this.imgSrc} alt="University Image"/>
        <Count by={1} onCountClickCallback={this.onCountClick}/>
        <Count by={5} onCountClickCallback={this.onCountClick}/>
        <Count by={10} onCountClickCallback={this.onCountClick}/>
        <h4>{this.state.cnt}</h4>
      </div>
    );
  }  
}

export default App;
