import LoginComponent from "./login";
import React, { Component } from "react";
import './login.css'
import '../../common.css'

class Todo extends Component{
    constructor(props){
        super(props);
        this.generateTodos = this.generateTodos.bind(this);
    }

    render(){
        return(
            <div className="todo">
                <HeaderComponent/>
                <ul className="list-group">
                    {this.generateTodos()}
                </ul>
                <footer className="fixed-footer">
                    <h4>For Issues, Contact Us</h4>
                </footer>
            </div>
        );
    }

    generateTodos(){
        let todos =
        [
            {id: 0, task: 'Eat'},
            {id: 1, task: 'Sleep'},
            {id: 2, task: 'Code'},
            {id: 3, task: 'Repeat'}
        ];

        //let ret = todos.map( todo => <li key={todo.id} className="list-group-item"> {todo.task} </li>);
        let ret = [];
        for(let i=0;i<todos.length;i++){
            let todo = todos[i];
            ret.push(<li key={todo.id} className="list-group-item"> {todo.task} </li>);
        }
        console.log(ret);
        return ret;
    }
}

class HeaderComponent extends Component{
    render(){
        return (
            <header>
                <nav className="navbar navbar-expand-md">
                    <div> The App </div>
                    <ul className="navbar-nav">
                        <li className="nav-link">Home</li>
                        <li className="nav-link">Todos</li>
                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-link">Login</li>
                        <li className="nav-link">Logout</li>
                    </ul>
                </nav>

            </header>
        )
    }
}

export default Todo;
