import React, { Component } from "react";
import './Count.css'
import PropTypes from 'prop-types'


class Counter extends Component{
    render(){
        return (
          <div className="App">
            <h1> Hello World </h1>
            <img src={this.imgSrc} alt="University Image"/>
            <Count by={1} onCountClickCallback={this.onCountClick}/>
            <Count by={5} onCountClickCallback={this.onCountClick}/>
            <Count by={10} onCountClickCallback={this.onCountClick}/>
            <h4>{this.state.cnt}</h4>
          </div>
        );
      }
      
      onCountClick = (by) => {
        //this.setState({cnt: this.state.cnt + by});
    
        let c = 2;
    
        this.setState(function(state,props){
          return {cnt : state.cnt + by + c};
        });
      };
}

class Count extends Component{
    constructor(props){
        super(props);
        //console.log(props)
    }
    
    render(){
        return(
            <div>
            <button type="button" className="btn btn-success" onClick=
                {
                 () => this.props.onCountClickCallback(this.props.by)
                }
                >+{this.props.by}</button>
            </div>
        );
    }
}

Count.defaultProps = {
    by: 1
}

export default Counter
